import React, { Component } from 'react';
import { strict } from 'assert';
//import './Ficha_Cadastro.css';


class Ficha_Cadastro extends Component {
    constructor(props){
        super(props);
        this.handleNameChange=this.handleNameChange.bind(this)
        this.handleEmailChange=this.handleEmailChange.bind(this)
        this.handleCpfChange=this.handleCpfChange.bind(this)
        this.handleDataNascimentoChange=this.handleDataNascimentoChange.bind(this)
        this.handleTelefoneChange=this.handleTelefoneChange.bind(this)
    }

    handleNameChange(e) {
        this.props.onNameChange(e.target.value);
     }
    
    handleEmailChange(e) {
        this.props.onEmailChange(e.target.value);
    }
    
    handleCpfChange(e) {
        this.props.onCpfChange(e.target.value);
    }
    
    handleDataNascimentoChange(e) {
        this.props.onDataNascimentoChange(e.target.value);
    }
    
    handleTelefoneChange(e) {
        this.props.onTelefoneChange(e.target.value);
    }

    MascaraCPF(cpf){
        if(mascaraInteiro(cpf)==false){
                event.returnValue = false;
        }       
        return formataCampo(cpf, '000.000.000-00', event);
    }

    mascaraInteiro(){
        if (event.keyCode < 48 || event.keyCode > 57){
                event.returnValue = false;
                return false;
        }
        return true;
    }
    
    formataCampo(campo, Mascara, evento) { 
        var boleanoMascara; 

        var Digitato = evento.keyCode;
        exp = /\-|\.|\/|\(|\)| /g
        campoSoNumeros = campo.value.toString().replace( exp, "" ); 

        var posicaoCampo = 0;    
        var NovoValorCampo="";
        var TamanhoMascara = campoSoNumeros.length;; 

        if (Digitato != 8) { // backspace 
                for(i=0; i<= TamanhoMascara; i++) { 
                        boleanoMascara  = ((Mascara.charAt(i) == "-") || (Mascara.charAt(i) == ".")
                                                                || (Mascara.charAt(i) == "/")) 
                        boleanoMascara  = boleanoMascara || ((Mascara.charAt(i) == "(") 
                                                                || (Mascara.charAt(i) == ")") || (Mascara.charAt(i) == " ")) 
                        if (boleanoMascara) { 
                                NovoValorCampo += Mascara.charAt(i); 
                                  TamanhoMascara++;
                        }else { 
                                NovoValorCampo += campoSoNumeros.charAt(posicaoCampo); 
                                posicaoCampo++; 
                          }              
                  }      
                campo.value = NovoValorCampo;
                  return true; 
        }else { 
                return true; 
        }
    }

    render() {
    if ((this.props.cpf).length == 11) {
        // this.props.cpf = ((this.props.cpf).substring(1,3))+"."+((this.props.cpf).substring(4,6))+"."+((this.props.cpf).substring(7,9))+"-"+((this.props.cpf).substring(10,11))
    }
    return (

        <div className='Ficha'>
            <h1>Ficha de Cadastro de Novo Aluno:</h1>
                <label>Nome</label><br/>
                <input type="text" name="nome" value={this.props.nome} onChange={this.handleNameChange}></input><br/>
                <label>E-mail</label><br/>
                <input type="doc" name="email" value={this.props.email} onChange={this.handleEmailChange}></input><br/>
                <label>CPF</label><br/>
                <input type="text" name="cpf" value={this.props.cpf} onChange={this.handleCpfChange}></input><br/>
                <label>Data de nascimento</label><br/>
                <input type="date" name="dataNascimento" value={this.props.dataNascimento} onChange={this.handleDataNascimentoChange}></input><br/>
                <label>Telefone</label><br/>
                <input type="number" name="telefone" value={this.props.telefone} onChange={this.handleTelefoneChange}></input><br/>
            <h2>{formataCampo((this.props.cpf),this.MascaraCPF,event)}</h2>
        </div>
    );
    }
}
export default Ficha_Cadastro;
