import React, { Component } from 'react';
import logo from './airplane.png';
import './Cadastro.css';
import { Link } from 'react-router-dom'
import Ficha_Cadastro from './Ficha_Cadastro'

class Cadastro extends Component {
 constructor(props) {
   super(props);
   this.handleChange=this.handleChange.bind(this)
   this.state = {
      nome: '',
      email: '',
      cpf: '',
      dataNascimento: '',
      telefone: '',
  }
 }

 handleChange(nome,email,cpf,dataNascimento,telefone) {
    this.setState({
       nome: nome,
       email: email,
       cpf: cpf,
       dataNascimento: dataNascimento,
       telefone: telefone,
   })
 }

 render() {
    const nome = this.state.nome
    const email = this.state.email
    const cpf = this.state.cpf
    const dataNascimento = this.state.dataNascimento
    const telefone = this.state.telefone
 return (
    <div className="pagina">
       <Ficha_Cadastro 
       nome={nome}
       email={email}
       cpf={cpf}
       dataNascimento={dataNascimento}
       telefone={telefone}
       onInputChange={this.handleChange}/>
       <button>
          <Link to="/">Voltar</Link>
       </button>
    </div>
 );
 }
}
export default Cadastro;
