import React, { Component } from 'react';
//import './Ficha_Cadastro.css';


class Ficha_Cadastro extends Component {
    constructor(props){
        super(props);
        this.handleChange = this.handleChange.bind(this);
    }

    handleChange(e) {
        this.props.onInputChange(e.target.value);
    }

    handleSubmit = event => {
        
        event.preventDefault();
    
        const user = {
            nome: this.props.nome,
            email: this.props.email,
            cpf: this.props.cpf,
            dataNascimento: this.props.dataNascimento,
            telefone: this.props.telefone,
        };

        console.log(user);
    }

render() {
return (
    <div className='Ficha'>
        <h1>Ficha de Cadastro de Novo Aluno:</h1>
        <form onSubmit={this.handleSubmit}>
            <label>Nome</label><br/>
            <input type="text" name="nome" value={this.user.nome} onChange={this.handleChange}></input><br/>
            <label>E-mail</label><br/>
            <input type="email" name="email" value={this.user.email} onChange={this.handleChange}></input><br/>
            <label>CPF</label><br/>
            <input type="text" name="cpf" value={this.user.cpf} onChange={this.handleChange}></input><br/>
            <label>Data de nascimento</label><br/>
            <input type="date" name="dataNascimento" value={this.user.dataNascimento} onChange={this.handleChange}></input><br/>
            <label>Telefone</label><br/>
            <input type="tel" name="telefone" value={this.user.telefone} onChange={this.handleChange}></input><br/>
            <button type="submit">Enviar</button> 
        </form>
    </div>
 );
 }
}
export default Ficha_Cadastro;
