import React, { Component } from 'react';
import MaskedInput from 'react-text-mask'
import '../css/Ficha_Avalia_Voo.css';


class Ficha_Avalia_Voo extends Component {
    constructor(props){
        super(props);
        this.handleDataVooChange=this.handleDataVooChange.bind(this);
        this.handleHoraVooChange=this.handleHoraVooChange.bind(this);
        this.handleHorasTotalChange=this.handleHorasTotalChange.bind(this);
        this.handleNotaChange=this.handleNotaChange.bind(this);
        this.handleInstrutorIdChange=this.handleInstrutorIdChange.bind(this);
        this.handleAlunoIdChange=this.handleAlunoIdChange.bind(this);
    }

    handleDataVooChange(e) {
        this.props.onDataVooChange(e.target.value);
     }
    
     handleHoraVooChange(e) {
        this.props.onHoraVooChange(e.target.value);
    }
    
    handleHorasTotalChange(e) {
        this.props.onHotasTotalChange(e.target.value);
    }
    
    handleNotaChange(e) {
        this.props.onNotaChange(e.target.value);
    }

    handleInstrutorIdChange(e) {
        this.props.onInstrutorIdChange(e.target.value);
    }
    
    handleAlunoIdChange(e) {
        this.props.onAlunoIdChange(e.target.value);
    }

    render() {
        return (
            <div className='Ficha'>
                <h1>Ficha de Cadastro de Novo Aluno:</h1>
                    <label>Número Cadastro Instrutor</label>
                    <input  type="text" name="instrutor_id" value={this.props.instrutor_id} onChange={this.handleInstrutorIdChange}></input><br/>
                    <label>E-mail</label><br/>
                    <input type="text" name="email" value={this.props.email} onChange={this.handleEmailChange}></input><br/>
                    <label>CPF (apenas números)</label><br/>
                    <input type="text" name="cpf" value={this.props.cpf} onChange={this.handleCpfChange}></input><br/>
                    <label>Data de nascimento (dd/mm/yyyy)</label><br/>
                    <input type="text" name="dataNascimento" value={this.props.dataNascimento} onChange={this.handleDataNascimentoChange}></input><br/>
                    <label>Endereço</label><br/>
                    <input type="text" name="endereco" value={this.props.endereco} onChange={this.handleEnderecoChange}></input><br/>
                    <label>Telefone</label><br/>
                    <input type="text" name="telefone" value={this.props.telefone} onChange={this.handleTelefoneChange}></input><br/>
            </div>
        );
    }
}

export default Ficha_Avalia_Voo;


