import React, { Component } from 'react';
import axios from 'axios';
//import '../css/paginaCadastro.css';
import { Link, Redirect } from 'react-router-dom';
//import Ficha_Cadastro from '../components/Ficha_Cadastro';
//import Cadastro_Sucesso from '../components/Cadastro_Sucesso';

class paginaBusca extends Component {
 constructor(props) {
   super(props);
   this.handleCpfChange=this.handleCpfChange.bind(this);
   this.state = {
      nome: '',
      email: '',
      cpf: '',
      dataNascimento: '',
      telefone: '',
      success: false,
  };
 }

handleCpfChange(e) {
   this.setState({cpf: e.target.value,});
}

handleSubmit = event => {
   event.preventDefault();

   const user = {
     data: {
        cpf: this.state.cpf
     }
   };
   
   axios.get(`https://testeparaaviacao.herokuapp.com/api/Aluno`,user)
     .then(res => {
          if (res.data.status === 'success')
             this.setState({nome: res.data.nome});
             this.setState({email: res.data.email});   
             this.setState({dataNascimento: res.data.dataNascimento});
             this.setState({telefone: res.data.telefone});
             this.setState({success: true});
          console.log(res.data.status);
          console.log(res);
          console.log(res.data);
    })
     .catch(function (error) {
          if (error.response) {
             alert(error.response.data.message);
             console.log(error.response.status);
             console.log(error.response.headers);
          } else if (error.request) {
             console.log(error.request);
          } else {

             console.log('Error', error.message);
          }
          console.log(error.config);
     })
 }

 render() {
  if (this.state.success === true){
      // return <Busca_Sucesso />
  }
    return (
        <div className="pagina">
            <form onSubmit={this.handleSubmit}>
                <h1>Digite o CPF do aluno que deseja buscar: </h1>
                <label>CPF</label><br/>
                <input type="text" name="cpf" value={this.state.cpf} onChange={this.handleCpfChange}></input><br/>
                <button id="buttonSuccess" type="submit">Enviar</button>
                <button id="buttonDanger">
                   <Link id="link" to="/">Voltar</Link>
               </button>
           </form>
       </div>
  );
}

}
export default paginaBusca;
