import React, { Component } from 'react';
//import './Ficha_Cadastro.css';


class Ficha_Cadastro extends Component {
    constructor(props){
        super(props);
        this.handleChange = this.handleChange.bind(this);
    }

    handleChange(e) {
        this.props.onInputChange(e.target.value);
    }

render() {
   return (
        <div className='Ficha'>
        <h1>Ficha de Cadastro de Novo Aluno:</h1>
            <label>Nome</label><br/>
            <input type="text" name="nome" value={this.props.nome} onChange={this.handleChange}></input><br/>
            <label>E-mail</label><br/>
            <input type="email" name="email" value={this.props.email} onChange={this.handleChange}></input><br/>
            <label>CPF</label><br/>
            <input type="text" name="cpf" value={this.props.cpf} onChange={this.handleChange}></input><br/>
            <label>Data de nascimento</label><br/>
            <input type="date" name="dataNascimento" value={this.props.dataNascimento} onChange={this.handleChange}></input><br/>
            <label>Telefone</label><br/>
            <input type="tel" name="telefone" value={this.props.telefone} onChange={this.handleChange}></input><br/>
            <button type="submit">Enviar</button> 
    </div>
 );
 }
}
export default Ficha_Cadastro;
