 }

 render() {
   /*if (this.state.redirect === true){
       return <Cadastro_Sucesso funcao="Aluno" chave="Número de Matrícula" num_matric={this.state.num_matric} nome={this.state.nome}/>
   }*/
   return (
      <div className="pagina">
      <Naveg/>
      <form onSubmit={this.handleSubmit}>
          <h1>Busca Voo Aluno</h1>
          <select type="text" name="escolha" value={this.props.escolha} onChange={this.handleEscolhaChange}>
                        <option value={''}>...</option>
                        <option value={1}>Id Voo</option>
                        <option value={2}>Número De Matrícula</option>
          </select> 
          <label>Digite Seu Número de Matricula</label><br/>
          <input type="text" name="cpf" value={this.state.num_matric} onChange={this.handleNumMatricChange}></input><br/>
          <button id="buttonSuccess" type="submit">Enviar</button>
     </form>
     <button id="buttonDanger1">
             <Link id="link" to="/aluno">Voltar</Link>
     </button>
     </div>
  );
}

}
export default paginaBuscaVooAluno;
